
public class DataTypeTest {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte a = -128;
		byte b = 127;
		byte c = 0;
		
		char d = '\u0001';
		String e = "\u0001"; 
		
		System.out.println("Byte Data a: " + a + "\nByte Data b: " + b
				+ "\nByte Data c: " + c);
		System.out.println("Char Data d: " + d + "\nString Data e: " + e);


	}

}
